import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.find.js";
import "core-js/modules/es.iterator.for-each.js";
import MapSvg from '@/assets/map.svg';
export default {
  components: {
    MapSvg
  },
  props: {
    regionItems: {
      type: Array,
      required: true,
      default: () => []
    }
  },
  data() {
    return {
      selectedRegion: null,
      tooltipVisible: false,
      tooltipText: '',
      tooltipX: 0,
      tooltipY: 0,
      defaultViewBox: '0 0 29252.3 16557.2',
      currentViewBox: '0 0 29252.3 16557.2',
      zoomLevel: 1
    };
  },
  mounted() {
    this.updateSvgText();
    this.updateSvgClasses();
    this.addEventListeners();
  },
  methods: {
    addEventListeners() {
      const svg = this.$el.querySelector('.svg-map');
      if (!svg) return;
      const paths = svg.querySelectorAll('path');
      paths.forEach(path => {
        path.addEventListener('click', () => {
          const regionId = this.getRegionIdFromClass(path);
          if (regionId) {
            this.selectRegion(regionId);
          }
        });
        path.addEventListener('mouseenter', event => {
          const regionId = this.getRegionIdFromClass(path);
          const region = this.regionItems.find(r => String(r.id) === regionId);
          const regionName = region ? region.name : 'Неизвестный регион';
          console.log('MouseEnter:', {
            regionId,
            region,
            regionName
          });
          this.showTooltip(regionName, event);
        });
        path.addEventListener('mouseleave', () => {
          this.hideTooltip();
        });
      });
    },
    handleSvgClick(event) {
      const path = event.target.closest('path');
      if (path) {
        const regionId = this.getRegionIdFromClass(path);
        if (regionId) {
          this.selectRegion(regionId);
        }
      }
    },
    getRegionIdFromClass(element) {
      const classList = element.classList;
      const filClass = Array.from(classList).find(cls => cls.startsWith('fil'));
      const regionId = filClass ? filClass.replace('fil', '') : null;
      console.log('getRegionIdFromClass:', {
        element,
        filClass,
        regionId
      });
      return regionId;
    },
    selectRegion(regionId) {
      this.selectedRegion = regionId;
      this.centerRegion(regionId);
      this.updateSvgClasses();
      this.$emit('region-selected', regionId);
    },
    resetToDefault() {
      this.selectedRegion = null;
      this.currentViewBox = this.defaultViewBox;
      this.zoomLevel = 1;
      this.updateSvgClasses();
      this.updateSvgViewBox();
      this.$emit('region-selected', null);
    },
    centerRegion(regionId) {
      const svg = this.$el.querySelector('.svg-map');
      const path = svg.querySelector(`.fil${regionId}`);
      if (path) {
        const bbox = path.getBBox();
        const container = this.$el.querySelector('.map-container');
        let zoomFactor = 1.5;
        if (container) {
          const containerWidth = container.clientWidth;
          const containerHeight = container.clientHeight;
          zoomFactor = Math.min(containerWidth / bbox.width, containerHeight / bbox.height) * 0.8;
        }
        const newWidth = bbox.width * zoomFactor;
        const newHeight = bbox.height;
        const centerX = bbox.x + bbox.width / 2;
        const centerY = bbox.y + bbox.height / 2;
        const newX = centerX - newWidth / 2;
        const newY = centerY - newHeight / 2;
        this.currentViewBox = `${newX} ${newY} ${newWidth} ${newHeight}`;
        this.zoomLevel = zoomFactor;
      }
    },
    updateSvgViewBox() {
      const svg = this.$el.querySelector('.svg-map');
      if (svg) {
        svg.setAttribute('viewBox', this.currentViewBox);
      }
    },
    updateSvgClasses() {
      const svg = this.$el.querySelector('.svg-map');
      if (!svg) return;
      const paths = svg.querySelectorAll('path');
      paths.forEach(path => {
        const regionId = this.getRegionIdFromClass(path);
        if (this.selectedRegion !== null && regionId !== this.selectedRegion) {
          path.classList.add('hidden');
        } else {
          path.classList.remove('hidden');
        }
      });
      const texts = svg.querySelectorAll('text');
      texts.forEach(text => {
        const regionId = this.getRegionIdFromClass(text);
        if (this.selectedRegion !== null && regionId !== this.selectedRegion) {
          text.classList.add('hidden');
        } else {
          text.classList.remove('hidden');
        }
      });
    },
    updateSvgText() {
      const svg = this.$el.querySelector('.svg-map');
      if (!svg) return;
      const texts = svg.querySelectorAll('text');
      texts.forEach(text => {
        const regionId = this.getRegionIdFromClass(text);
        const region = this.regionItems.find(r => r.id === regionId);
        if (region && region.percentage) {
          text.textContent = `${region.percentage}%`;
        }
      });
    },
    showTooltip(text, event) {
      this.tooltipText = text;
      this.tooltipVisible = true;
      this.tooltipX = event.clientX;
      this.tooltipY = event.clientY;
    },
    hideTooltip() {
      this.tooltipVisible = false;
    }
  },
  watch: {
    selectedRegion() {
      this.updateSvgClasses();
    },
    regionItems: {
      handler() {
        this.updateSvgText();
      },
      deep: true
    }
  }
};
