      type: Object,
      required: true,
      default: () => ({
        series: [],
        categories: [],
        colors: [],
        percentChanges: []
      })
    }
  },
  data() {
    return {
      customLabels: []
    };
  },
  computed: {
    chartOptions() {
      return {
        chart: {
          type: 'bar',
          stacked: true,
          height: this.height,
          animations: {
            enabled: true,
            speed: 800
          },
          toolbar: {
            show: false
          }
        },
        plotOptions: {
          bar: {
            horizontal: true,
            barHeight: '70%',
            dataLabels: {
              position: 'center'
            }
          }
        },
        dataLabels: {
          enabled: true,
          offsetX: -6,
          style: {
            fontSize: '11px',
            fontWeight: 400,
            fontFamily: 'Montserrat-Regular'
          }
        },
        grid: {
          show: false
        },
        stroke: {
          show: false
        },
        tooltip: {
          shared: false,
          intersect: true,
          custom: ({
            series,
            seriesIndex,
            dataPointIndex,
            w
          }) => {
            const category = w.config.xaxis.categories[dataPointIndex];
            const value = series[seriesIndex][dataPointIndex];
            const color = w.globals.colors[seriesIndex];
            return `
              <div style="padding: 5px; background: ${color}; color: #fff; font-size: 10px;">
                <strong>${category}:</strong> ${value}
              </div>
            `;
          }
        },
        xaxis: {
          categories: this.dataSource.categories,
          labels: {
            style: {
              colors: '#ffffff',
              fontSize: '11px'
            }
          },
          axisBorder: {
            show: true,
            color: '#4b3f3f'
          },
          axisTicks: {
            show: true,
            color: '#4b3f3f'
          }
        },
        yaxis: {
          labels: {
            offsetX: 5,
            style: {
              colors: '#ffffff'
            }
          },
