import ApexChart from 'vue-apexcharts';
export default {
  name: 'PopulationChart',
  components: {
    ApexChart
  },
  props: {
    width: {
      type: String,
      default: '100%'
    },
    height: {
      type: String,
      default: '100%'
    },
    dataSource: {
      type: Object,
      required: true,
      default: () => ({
        series: [],
        categories: [],
        colors: []
      })
    }
  },
  data() {
    return {
      horizontalTotal: true
    };
  },
  computed: {
    chartOptions() {
      return {
        chart: {
          type: 'bar',
          height: 350,
          stacked: true,
          toolbar: {
            show: false
          }
        },
        plotOptions: {
          bar: {
            horizontal: true,
            dataLabels: {
              position: 'center',
              total: {
                enabled: true,
                offsetX: 3,
                formatter: function (val) {
                  return val.toFixed(1);
                },
                style: {
                  fontSize: '11px',
                  color: '#ffffff',
                  fontFamily: 'Montserrat-Regular, sans-serif',
                  fontWeight: 400
                }
              }
            }
          }
        },
        dataLabels: {
          enabled: true,
          style: {
            fontFamily: 'Montserrat-Regular, sans-serif',
            fontSize: '11px',
            fontWeight: 300,
            colors: ['#ffffff']
          }
        },
        grid: {
          show: false
        },
        stroke: {
          width: 0,
          colors: ['#fff']
        },
        xaxis: {
          tickAmount: 6,
          categories: this.dataSource.categories,
          labels: {
            style: {
              colors: '#ffffff',
              fontSize: '11px'
            }
          },
          axisBorder: {
            show: true,
            color: '#4b3f3f'
          },
          axisTicks: {
            show: true,
            color: '#4b3f3f'
          }
        },
        yaxis: {
          axisBorder: {
            show: true,
            color: '#4b3f3f'
          },
          axisTicks: {
            show: true,
            color: '#4b3f3f'
          },
          labels: {
            show: true,
            offsetX: 5,
            formatter: function (val) {
              return val;
            },
            style: {
              colors: '#ffffff',
              fontSize: '10px'
            }
          }
        },
        tooltip: {
          shared: false,
          intersect: true,
          style: {
            fontFamily: 'Montserrat-Regular, sans-serif',
            fontSize: '10px',
            fontWeight: 400
          },
          custom: function ({
            series,
            seriesIndex,
            dataPointIndex,
            w
          }) {
            const value = series[seriesIndex][dataPointIndex];
            const category = w.config.xaxis.categories[dataPointIndex];
            const color = w.globals.colors[seriesIndex];
            return `
                <div style="padding: 5px; background: ${color}; color: #fff; border-radius: 0; font-size: 10px; border: none;">
                    <strong>${category}:</strong> ${value}
                </div>
            `;
          }
        },
        fill: {
          opacity: 1
        },
        legend: {
          position: 'bottom',
          fontSize: '10px',
          markers: {
            width: 10,
            height: 10,
            borderRadius: 10,
            strokeWidth: 0
          },
          offsetX: 40,
          formatter: function (seriesName, opts) {
            return `<span style="color: ${opts.w.globals.colors[opts.seriesIndex]}">${seriesName}</span>`;
          }
        },
        colors: this.dataSource.colors
      };
    }
  }
};
